import React, { useState } from "react";
import { useMoralis, useMoralisQuery } from "react-moralis";
import { getNativeByChain } from "helpers/networks";
import { Card, Image, Tooltip, Modal, Badge, Alert } from "antd";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import { FileSearchOutlined, RightCircleOutlined, ShoppingCartOutlined } from "@ant-design/icons";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";
import { getExplorer } from "helpers/networks";
import AddressInput from "./AddressInput";
import { getCollectionsByChain } from "helpers/collections";
import { useWeb3ExecuteFunction } from "react-moralis";
const { Meta } = Card;

const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
};

function NFTTokenIds({inputValue, setInputValue}) {
  const { NFTTokenIds } = useNFTTokenIds(inputValue);
  const { chainId, contractABI, marketAddress, walletAddress } = useMoralisDapp();
  const { Moralis } = useMoralis();
  const [visible, setVisibility] = useState(false);
  const [nftToSend, setNftToSend] = useState(null);
  const nativeName = getNativeByChain(chainId);
  const purchaseItemFunction = "createMarketSale";
  const contractProcessor = useWeb3ExecuteFunction();
  const contractABIJson = JSON.parse(contractABI);
  const NFTCollections = getCollectionsByChain(chainId)
  const [nftToBuy, setNftToBuy] = useState();
  const queryMarketItems = useMoralisQuery("CreatedMarketItems");
  const fetchMarketItems = JSON.parse(
    JSON.stringify(queryMarketItems.data, [
      "objectId",
      "createdAt",
      "price",
      "itemId",
      "sold",
      "tokenId",
      "seller",
      "owner",
      "confirmed"
    ])
  );

  async function purchase() {
    const tokenDetails = getMarketItem(nftToBuy);
    const itemID = tokenDetails.price;
    const tokenPrice = tokenDetails.price;
    const ops = {
      contractAddress: marketAddress,
      functionName: purchaseItemFunction,
      abi: contractABIJson,
      params : {
        nftContract: nftToBuy.token_address,
        itemId: itemID
      },
      msgValue: tokenPrice
    };

    await contractProcessor.fetch({
      params: ops,
      onSuccess: () => {
        alert("Bought Thid NFT");
        updateSoldMarketItem();
      },
      onError: (error) => {
        alert(error);
      }
    })
  }

  const handleBuyClick = (nft) => {
    setNftToBuy(nft);
    setVisibility(true);
  };

  async function updateSoldMarketItem() {
    const id = getMarketItem(nftToBuy).objectId
    const marketList = Moralis.Object.extend("CreatedMarketItems");
    const query = new Moralis.Query(marketList);
    await query.get(id)
    .then(obj => {
      obj.set("sold", true);
      obj.set("owner", walletAddress);
      obj.save();
    });
  }

const getMarketItem = (nft) => {
  const result = fetchMarketItems?.find(
    (e) =>
    e.nftContract === nft?.token_address &&
    e.tokenId === nft?.token_id &&
    e.sold === false &&
    e.confirmed === true
  )
  return result
}

  console.log(NFTTokenIds);
  return (
    <>
      <div style={styles.NFTs}>
        {NFTTokenIds && inputValue !== "explore" &&
          NFTTokenIds.map((nft, index) => (
            <Card
              hoverable
              actions={[
                <Tooltip title="View On Blockexplorer">
                  <FileSearchOutlined
                    onClick={() =>
                      window.open(`${getExplorer(chainId)}address/${nft.token_address}`, "_blank")
                    }
                  />
                </Tooltip>,
               
                <Tooltip title="Buy this NFT">
                  <ShoppingCartOutlined onClick={() => handleBuyClick(nft)} />
                </Tooltip>,
              ]}
              style={{ width: 240, border: "2px solid #e7eaf3" }}
              cover={
                <Image
                  preview={false}
                  src={nft?.image || "error"}
                  fallback="data:image/png;base64,"
                  alt=""
                  style={{ height: "300px" }}
                />
              }
              key={index}
            >
              {getMarketItem(nft) && <Badge.Ribbon text="Buy Now" color="green"></Badge.Ribbon>}
              <Meta title={nft.name} description={'#$(nft.token_id)'} />
            </Card>
          ))}

        {NFTCollections && inputValue === "explore" &&
                NFTCollections.map((nft, index) => (
                    <Card
                    hoverable
                    actions={[
                        <Tooltip title="View this Collection">
                        <RightCircleOutlined onClick={() => setInputValue(nft.addrs)} />
                        </Tooltip>,
                    ]}
                    style={{ width: 240, border: "2px solid #e7eaf3" }}
                    cover={
                        <Image
                        preview={false}
                        src={nft?.image || "error"}
                        fallback="data:image/png;base64,"
                        alt=""
                        style={{ height: "300px" }}
                        />
                    }
                    key={index}
                    >
                    <Meta title={nft.name} />
                    </Card>
                ))}
      </div>
      {getMarketItem(nftToBuy) ? (
         <Modal
         title={`Buy ${nftToBuy?.name || "NFT"}`}
         visible={visible}
         onCancel={() => setVisibility(false)}
         onOk={() => purchase()}
         okText="Buy"
       >
         <div style={{
           width: "250px",
           margin: "auto",
         }}>
         <Badge.Ribbon text={'${getMarketItem(nftToBuy).price / ("1e" + 18) } $nativeName'} color="green">
     <img 
         src={nftToBuy?.image}
         style={{
             width: "250px",
             margin: "auto",
             borderRadius: "10px",
             marginBottom: "15px",
         }}
     
     /></Badge.Ribbon>
     </div>
       </Modal>

      ) : (
        <Modal
        title={`Buy ${nftToBuy?.name || "NFT"}`}
        visible={visible}
        onCancel={() => setVisibility(false)}
        onOk={() => setVisibility(false)}
        okText="Ok"
      >
    <img 
        src={nftToBuy?.image}
        style={{
            width: "250px",
            margin: "auto",
            borderRadius: "10px",
            marginBottom: "15px",
        }}
    
    />
    <Alert
    message = "This NFT is currently not for sale"
    type = "warning"
    />
      </Modal>
      )
      
      
      }
     
    </>
  );
}

export default NFTTokenIds;
